eader-row .right-nav ul li {\n  margin-right: 2rem;\n  @include gridle_show_inline ();\n\n  &:last-child {\n    margin-right: 0;\n  }\n  @include gridle_state(mobile mid-tablet) {\n     @include gridle_hide ();\n  }\n}\n\n.right-nav-theme {\n  height: calc(100vh - var(--mainContainerMarginTop) - 6rem);\n  @include gridle_row();\n  @include gridle_row_align(middle);\n}\n\n.right-nav-theme a{\n  transition:0.2s opacity;\n  opacity:0.5;\n  transition:0.2s opacity\n}\n\n.right-nav-theme a:hover{\n  transition:0.2 opacity;\n  opacity:1;\n}\n\n.mobile_overlay_wrap {\n  width: 100%;\n  position: fixed;\n  z-index: 999;\n  height: 100%;\n  top: 0;\n  left: 0;\n  background-color: #1a1a1a;\n}\n\n.mobile_button_row {\n  @include gridle_hide ();\n  @include gridle_row();\n  @include gridle_row_align(center middle)\n  @include gridle_state(mobile mid-tablet) {\n     display:flex;\n  }\n}\n\n.mobile_button {\n\n  position: fixed;\n  line-height: 0;\n  bottom: 7vh;\n  width: 4rem;\n  height: 4rem;\n  font-size: 1rem;\n  border-radius: 50%;\n  cursor: pointer;\n  background-color: #007AFF;\n  z-index: 999;\n  color: white;\n  text-align: center;\n  align-items:center;\n  justify-content: center;\n  display: flex;\n}\n\nnav ul {\n  list-style-type: none;\n}\n\n.mouter {\n  max-width:1366px;\n  margin:0 auto;\n\n}\n\n.minner{\n  margin-top:75px;\n  margin-left: calc(var(--mainContainerMarginLeftRight));\n  margin-right: calc(var(--mainContainerMarginLeftRight));\n  margin-top: calc(var(--mainContainerMarginTop));\n  @include gridle_state(tablet) {\n    margin-left: calc(var(--mainContainerMarginLeftRight)/1.5);\n    margin-right: calc(var(--mainContainerMarginLeftRight)/1.5);\n    margin-top: calc(var(--mainContainerMarginTop));\n  }\n\n  @include gridle_state(mid-tablet) {\n    margin-left: calc(var(--mainContainerMarginLeftRight)/2);\n    margin-right: calc(var(--mainContainerMarginLeftRight)/2);\n    margin-top: calc(var(--mainContainerMarginTop));\n  }\n\n  @include gridle_state(mobile) {\n    margin-left: calc(var(--mainContainerMarginLeftRight)/3);\n    margin-right: calc(var(--mainContainerMarginLeftRight)/3);\n    margin-top: calc(var(--mainContainerMarginTop));\n  }\n}\n\n.fade-enter,\n.fade-leave {\n     -webkit-transition: opacity .5s ease-in;\n       -moz-transition: opacity .5s ease-in;\n        -ms-transition: opacity .5s ease-in;\n         -o-transition: opacity .5s ease-in;\n            transition: opacity .5s ease-in;\n}\n/* .fade-leave-active in <2.1.8 */\n.fade-enter-active,\n.fade-leave-to {\n  opacity: 1;\n}\n\n.page-enter-active {\n  animation: fade-in-bottom .3s;\n}\n.page-leave-active {\n  animation: fade-in-bottom .3s reverse;\n}\n\n.page-enter,.page-leave{\n  opacity:0;\n}\n\n@-webkit-keyframes fade-in-bottom {\n  0% {\n    -webkit-transform: translateY(50px);\n    transform: translateY(50px);\n    opacity: 0\n  }\n  100% {\n    -webkit-transform: translateY(0);\n    transform: translateY(0);\n    opacity: 1\n  }\n}\n\n@keyframes fade-in-bottom {\n  0% {\n    -webkit-transform: translateY(50px);\n    transform: translateY(50px);\n    opacity: 0\n  }\n  100% {\n    -webkit-transform: translateY(0);\n    transform: translateY(0);\n    opacity: 1\n  }\n}\n","// Flexbox Mixins\n// http://philipwalton.github.io/solved-by-flexbox/\n// https://github.com/philipwalton/solved-by-flexbox\n// \n// Copyright (c) 2013 Brian Franco\n//\n// Permission is hereby granted, free of charge, to any person obtaining a\n// copy of this software and associated documentation files (the\n// \"Software\"), to deal in the Software without restriction, including\n// without limitation the rights to use, copy, modify, merge, publish,\n// distribute, sublicense, and/or sell copies of the Software, and to\n// permit persons to whom the Software is furnished to do so, subject to\n// the following conditions:\n// The above copyright notice and this permission notice shall be included\n// in all copies or substantial portions of the Software.\n// THE SOFTWARE IS PROVIDED \"AS IS\", WITHOUT WARRANTY OF ANY KIND, EXPRESS\n// OR IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF\n// MERCHANTABILITY, FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT.\n// IN NO EVENT SHALL THE AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY\n// CLAIM, DAMAGES OR OTHER LIABILITY, WHETHER IN AN ACTION OF CONTRACT,\n// TORT OR OTHERWISE, ARISING FROM, OUT OF OR IN CONNECTION WITH THE\n// SOFTWARE OR THE USE OR OTHER DEALINGS IN THE SOFTWARE.\n//\n// This is a set of mixins for those who want to mess around with flexbox\n// using the native support of current browsers. For full support table\n// check: http://caniuse.com/flexbox\n//\n// Basically this will use:\n//\n// * Fallback, old syntax (IE10, mobile webkit browsers - no wrapping)\n// * Final standards syntax (FF, Safari, Chrome, IE11, Opera)\n//\n// This was inspired by:\n// \n// * http://dev.opera.com/articles/view/advanced-cross-browser-flexbox/\n// \n// With help from:\n// \n// * http://w3.org/tr/css3-flexbox/\n// * http://the-echoplex.net/flexyboxes/\n// * http://msdn.microsoft.com/en-us/library/ie/hh772069(v=vs.85).aspx\n// * http://css-tricks.com/using-flexbox/\n// * http://dev.opera.com/articles/view/advanced-cross-browser-flexbox/\n// * https://developer.mozilla.org/en-us/docs/web/guide/css/flexible_boxes\n\n//----------------------------------------------------------------------\n\n// Flexbox Containers\n//\n// The 'flex' value causes an element to generate a block-level flex\n// container box.\n//\n// The 'inline-flex' value causes an element to generate a inline-level\n// flex container box. \n//\n// display: flex | inline-flex\n//\n// http://w3.org/tr/css3-flexbox/#flex-containers\n//\n// (Placeholder selectors for each type, for those who rather @extend)\n\n@mixin flexbox {\n\t@if $gridle-vendor-prefix {\n\t\tdisplay: -webkit-box;\n\t\tdisplay: -webkit-flex;\n\t\tdisplay: -moz-flex;\n\t\tdisplay: -ms-flexbox;\n\t}\n\tdisplay: flex;\n}\n\n%flexbox { @include flexbox; }\n\n//----------------------------------\n\n@mixin inline-flex {\n\t@if $gridle-vendor-prefix {\n\t\tdisplay: -webkit-inline-box;\n\t\tdisplay: -webkit-inline-flex;\n\t\tdisplay: -moz-inline-flex;\n\t\tdisplay: -ms-inline-flexbox;\n\t}\n\tdisplay: inline-flex;\n}\n\n%inline-flex { @include inline-flex; }\n\n//----------------------------------------------------------------------\n\n// Flexbox Direction\n//\n// The 'flex-direction' property specifies how flex items are placed in\n// the flex container, by setting the direction of the flex container's\n// main axis. This determines the direction that flex items are laid out in. \n//\n// Values: row | row-reverse | column | column-reverse\n// Default: row\n//\n// http://w3.org/tr/css3-flexbox/#flex-direction-property\n\n@mixin flex-direction($value: row) {\n\t@if $gridle-vendor-prefix {\n\t\t@if $value == row-reverse {\n\t\t\t-webkit-box-direction: reverse;\n\t\t\t-webkit-box-orient: horizontal;\n\t\t} @else if $value == column {\n\t\t\t-webkit-box-direction: normal;\n\t\t\t-webkit-box-orient: vertical;\n\t\t} @else if $value == column-reverse {\n\t\t\t-webkit-box-direction: reverse;\n\t\t\t-webkit-box-orient: vertical;\n\t\t} @else {\n\t\t\t-webkit-box-direction: normal;\n\t\t\t-webkit-box-orient: horizontal;\n\t\t}\n\t\t-webkit-flex-direction: $value;\n\t\t-moz-flex-direction: $value;\n\t\t-ms-flex-direction: $value;\n\t}\n\tflex-direction: $value;\n}\n\t// Shorter version:\n\t@mixin flex-dir($args...) { @include flex-direction($args...); }\n\n//----------------------------------------------------------------------\n\n// Flexbox Wrap\n//\n// The 'flex-wrap' property controls whether the flex container is single-line\n// or multi-line, and the direction of the cross-axis, which determines\n// the direction new lines are stacked in. \n//\n// Values: nowrap | wrap | wrap-reverse\n// Default: nowrap\n//\n// http://w3.org/tr/css3-flexbox/#flex-wrap-property\n\n@mixin flex-wrap($value: nowrap) {\n\t@if $gridle-vendor-prefix {\n\t\t// No Webkit Box fallback.\n\t\t-webkit-flex-wrap: $value;\n\t\t-moz-flex-wrap: $value;\n\t\t@if $value == nowrap {\n\t\t\t-ms-flex-wrap: none;\n\t\t} @else { \n\t\t\t-ms-flex-wrap: $value; \n\t\t}\n\t}\n\tflex-wrap: $value;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox Flow (shorthand)\n//\n// The 'flex-flow' property is a shorthand for setting the 'flex-direction'\n// and 'flex-wrap' properties, which together define the flex container's\n// main and cross axes.\n//\n// Values: <flex-direction> | <flex-wrap> \n// Default: row nowrap\n//\n// http://w3.org/tr/css3-flexbox/#flex-flow-property\n\n@mixin flex-flow($values: (row nowrap)) {\n\t@if $gridle-vendor-prefix {\n\t\t// No Webkit Box fallback.\n\t\t-webkit-flex-flow: $values;\n\t\t-moz-flex-flow: $values;\n\t\t-ms-flex-flow: $values;\n\t}\n\tflex-flow: $values;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox Order\n//\n// The 'order' property controls the order in which flex items appear within\n// their flex container, by assigning them to ordinal groups.\n//\n// Default: 0\n//\n// http://w3.org/tr/css3-flexbox/#order-property\n\n@mixin order($int: 0) {\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-box-ordinal-group: $int + 1;\n\t\t-webkit-order: $int;\n\t\t-moz-order: $int;\n\t\t-ms-flex-order: $int;\n\t}\n\torder: $int;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox Grow\n//\n// The 'flex-grow' property sets the flex grow factor. Negative numbers\n// are invalid.\n//\n// Default: 0\n//\n// http://w3.org/tr/css3-flexbox/#flex-grow-property\n\n@mixin flex-grow($int: 0) {\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-box-flex: $int;\n\t\t-webkit-flex-grow: $int;\n\t\t-moz-flex-grow: $int;\n\t\t-ms-flex-positive: $int;\n\t}\n\tflex-grow: $int;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox Shrink\n//\n// The 'flex-shrink' property sets the flex shrink factor. Negative numbers\n// are invalid.\n//\n// Default: 1\n//\n// http://w3.org/tr/css3-flexbox/#flex-shrink-property\n\n@mixin flex-shrink($int: 1) {\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-flex-shrink: $int;\n\t\t-moz-flex-shrink: $int;\n\t\t-ms-flex-negative: $int;\n\t}\n\tflex-shrink: $int;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox Basis\n//\n// The 'flex-basis' property sets the flex basis. Negative lengths are invalid. \n//\n// Values: Like \"width\" \n// Default: auto\n//\n// http://www.w3.org/TR/css3-flexbox/#flex-basis-property\n\n@mixin flex-basis($value: auto) {\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-flex-basis: $value;\n\t\t-moz-flex-basis: $value;\n\t\t-ms-flex-preferred-size: $value;\n\t}\n\tflex-basis: $value;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox \"Flex\" (shorthand)\n//\n// The 'flex' property specifies the components of a flexible length: the\n// flex grow factor and flex shrink factor, and the flex basis. When an\n// element is a flex item, 'flex' is consulted instead of the main size\n// property to determine the main size of the element. If an element is\n// not a flex item, 'flex' has no effect.\n//\n// Values: none | <flex-grow> <flex-shrink> || <flex-basis>\n// Default: See individual properties (1 1 0).\n//\n// http://w3.org/tr/css3-flexbox/#flex-property\n\n@mixin flex($fg: 1, $fs: null, $fb: null) {\n    \n\t// Set a variable to be used by box-flex properties\n\t$fg-boxflex: $fg;\n\n\t// Box-Flex only supports a flex-grow value so let's grab the\n\t// first item in the list and just return that.\n\t@if type-of($fg) == 'list' {\n\t\t$fg-boxflex: nth($fg, 1);\n\t}\n\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-box-flex: $fg-boxflex;\n\t\t-webkit-flex: $fg $fs $fb;\n\t\t-moz-box-flex: $fg-boxflex;\n\t\t-moz-flex: $fg $fs $fb;\n\t\t-ms-flex: $fg $fs $fb;\n\t}\n\tflex: $fg $fs $fb;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox Justify Content\n//\n// The 'justify-content' property aligns flex items along the main axis\n// of the current line of the flex container. This is done after any flexible\n// lengths and any auto margins have been resolved. Typically it helps distribute\n// extra free space leftover when either all the flex items on a line are\n// inflexible, or are flexible but have reached their maximum size. It also\n// exerts some control over the alignment of items when they overflow the line.\n//\n// Note: 'space-*' values not supported in older syntaxes.\n//\n// Values: flex-start | flex-end | center | space-between | space-around\n// Default: flex-start\n//\n// http://w3.org/tr/css3-flexbox/#justify-content-property\n\n@mixin justify-content($value: flex-start) {\n\t@if $gridle-vendor-prefix {\n\t\t@if $value == flex-start {\n\t\t\t-webkit-box-pack: start;\n\t\t\t-ms-flex-pack: start;\n\t\t} @else if $value == flex-end {\n\t\t\t-webkit-box-pack: end;\n\t\t\t-ms-flex-pack: end;\n\t\t} @else if $value == space-between {\n\t\t\t-webkit-box-pack: justify;\n\t\t\t-ms-flex-pack: justify;\n\t\t} @else if $value == space-around {\n\t\t\t-ms-flex-pack: distribute;\t\t\n\t\t} @else {\n\t\t\t-webkit-box-pack: $value;\n\t\t\t-ms-flex-pack: $value;\n\t\t}\n\t\t-webkit-justify-content: $value;\n\t\t-moz-justify-content: $value;\n\t}\n\tjustify-content: $value;\n}\n\t// Shorter version:\n\t@mixin flex-just($args...) { @include justify-content($args...); }\n\n//----------------------------------------------------------------------\n\n// Flexbox Align Items\n//\n// Flex items can be aligned in the cross axis of the current line of the\n// flex container, similar to 'justify-content' but in the perpendicular\n// direction. 'align-items' sets the default alignment for all of the flex\n// container's items, including anonymous flex items. 'align-self' allows\n// this default alignment to be overridden for individual flex items. (For\n// anonymous flex items, 'align-self' always matches the value of 'align-items'\n// on their associated flex container.) \n//\n// Values: flex-start | flex-end | center | baseline | stretch\n// Default: stretch\n//\n// http://w3.org/tr/css3-flexbox/#align-items-property\n\n@mixin align-items($value: stretch) {\n\t@if $gridle-vendor-prefix {\n\t\t@if $value == flex-start {\n\t\t\t-webkit-box-align: start;\n\t\t\t-ms-flex-align: start;\n\t\t} @else if $value == flex-end {\n\t\t\t-webkit-box-align: end;\n\t\t\t-ms-flex-align: end;\n\t\t} @else {\n\t\t\t-webkit-box-align: $value;\n\t\t\t-ms-flex-align: $value;\n\t\t}\n\t\t-webkit-align-items: $value;\n\t\t-moz-align-items: $value;\n\t}\n\talign-items: $value;\n}\n\n//----------------------------------\n\n// Flexbox Align Self\n//\n// Values: auto | flex-start | flex-end | center | baseline | stretch\n// Default: auto\n\n@mixin align-self($value: auto) {\n\t@if $gridle-vendor-prefix {\n\t\t// No Webkit Box Fallback.\n\t\t-webkit-align-self: $value;\n\t\t-moz-align-self: $value;\n\t\t@if $value == flex-start {\n\t\t\t-ms-flex-item-align: start;\n\t\t} @else if $value == flex-end {\n\t\t\t-ms-flex-item-align: end;\n\t\t} @else {\n\t\t\t-ms-flex-item-align: $value;\n\t\t}\n\t}\n\talign-self: $value;\n}\n\n//----------------------------------------------------------------------\n\n// Flexbox Align Content\n//\n// The 'align-content' property aligns a flex container's lines within the\n// flex container when there is extra space in the cross-axis, similar to\n// how 'justify-content' aligns individual items within the main-axis. Note,\n// this property has no effect when the flexbox has only a single line.\n//\n// Values: flex-start | flex-end | center | space-between | space-around | stretch\n// Default: stretch\n//\n// http://w3.org/tr/css3-flexbox/#align-content-property\n\n@mixin align-content($value: stretch) {\n\t@if $gridle-vendor-prefix {\n\t\t// No Webkit Box Fallback.\n\t\t-webkit-align-content: $value;\n\t\t-moz-align-content: $value;\n\t\t@if $value == flex-start {\n\t\t\t-ms-flex-line-pack: start;\n\t\t} @else if $value == flex-end {\n\t\t\t-ms-flex-line-pack: end;\n\t\t} @else {\n\t\t\t-ms-flex-line-pack: $value;\n\t\t}\n\t}\n\talign-content: $value;\n}","//\n// Row\n//\n@mixin _gridle_common_row(\n\t$state : default\n) {\n\t@include flexbox;\n\t@include flex-wrap(wrap);\n\t@include flex(0 1 auto);\n}\n@mixin gridle_row(\n\t$reverse : false\n) {\n\t@include _gridle_call(row) {\n\t\t@if $reverse {\n\t\t\t@include flex-direction(row-reverse);\n\t\t} @else {\n\t\t\t@include flex-direction(row);\n\t\t}\n\t\tpadding-left: 0 !important;\n\t\tpadding-right: 0 !important;\n\t\tmargin-left: -#{gridle_get_state_var(gutter-left)};\n\t\tmargin-right: -#{gridle_get_state_var(gutter-right)};\n\t\n\t\t// handle row in no-gutter\n\t\t#{_gridle_get_generic_selector(no-gutter)} > & {\n\t\t\tmargin-left: 0 !important;\n\t\t\tmargin-right: 0 !important;\n\t\t}\n\n\t\t// do this only when not in generate phase\n\t\t// mean that we use the mixin directly\n\t\t@if _gridle_is_in_generate_phase() == false {\n\t\t\t@each $stateName, $state in gridle_get_states() {\n\t\t\t\t@include gridle_state($state) {\t\n\t\t\t\t\t& #{str-replace(#{&},\".state-#{gridle_get_state_var(name, $state)}\",\"\")} {\n\t\t\t\t\t\tmargin-left: -#{gridle_get_state_var(gutter-left, $state)};\n\t\t\t\t\t\tmargin-right: -#{gridle_get_state_var(gutter-right, $state)};\n\t\t\t\t\t}\n\t\t\t\t}\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Col\n//\n@mixin _gridle_common_col(\n\t$state : default\n) {\n}\n@mixin gridle_col(\n\t$reverse : false\n) {\n\t@include _gridle_call(col) {\n\t\t@if $reverse {\n\t\t\t@include flex-direction(column-reverse);\n\t\t} @else {\n\t\t\t@include flex-direction(column);\n\t\t}\n\t\tpadding-left: 0 !important;\n\t\tpadding-right: 0 !important;\n\t\tmargin-left: -#{gridle_get_state_var(gutter-left)};\n\t\tmargin-right: -#{gridle_get_state_var(gutter-right)};\n\n\t\t// do this only when not in generate phase\n\t\t// mean that we use the mixin directly\n\t\t@if _gridle_is_in_generate_phase() == false {\n\t\t\t@each $stateName, $state in gridle_get_states() {\n\t\t\t\t@include gridle_state($state) {\t\n\t\t\t\t\t& #{str-replace(#{&},\".state-#{gridle_get_state_var(name, $state)}\",\"\")} {\n\t\t\t\t\t\tmargin-left: -#{gridle_get_state_var(gutter-left, $state)};\n\t\t\t\t\t\tmargin-right: -#{gridle_get_state_var(gutter-right, $state)};\n\t\t\t\t\t}\n\t\t\t\t}\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Nowrap\n//\n@mixin _gridle_common_nowrap(\n\t$state : default\n) {\n\t@include flex-wrap(nowrap);\n\twhite-space:nowrap;\n\n\t> * {\n\t\twhite-space: normal;\n\t}\n}\n@mixin gridle_nowrap() {\n\t@include _gridle_call(nowrap) {\n\t}\n}\n\n\n//\n// Wrap\n//\n@mixin _gridle_common_wrap(\n\t$state : default\n) {\n\t@include flex-wrap(wrap);\n\twhite-space:normal;\n}\n@mixin gridle_wrap() {\n\t@include _gridle_call(wrap) {\n\t}\n}\n\n\n//\n// Grid\n//\n@mixin _gridle_common_grid(\n\t$state : default\n) {\n\t// @include flexbox;\n\t@include flex(0,0,auto);\n\t@include flex-direction(column); // reset direction for inner elements\n\tdisplay: block;\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-box-sizing: border-box;\n\t\t-moz-box-sizing: border-box;\n\t}\n\tbox-sizing: border-box;\n\t@include gridle_gutter();\n\t\n}\n@mixin gridle_grid(\n\t$columns,\n\t$context : null\n) {\n\t@if $columns == grow {\n\t\t@include gridle_grid_grow();\n\t} @else if $columns == adapt {\n\t\t@include gridle_grid_adapt();\n\t} @else {\n\n\t\t@include _gridle_call(grid) {\n\n\t\t\t// vars\n\t\t\t$name : gridle_get_state_var(name);\n\t\t\t@if type-of($context) != number {\n\t\t\t\t$context : gridle_get_state_var(context);\n\t\t\t}\n\t\t\t$name-multiplicator : gridle_get_state_var(name-multiplicator);\n\t\t\t$gutter-left : gridle_get_state_var(gutter-left);\n\t\t\t$gutter-right : gridle_get_state_var(gutter-right);\n\t\t\t$column-width : gridle_get_state_var(column-width);\n\t\t\t\n\t\t\t// manage columns\n\t\t\t@if type-of($columns) != number and map-has-key($_gridle_columns, $columns) {\n\t\t\t\t// the columns is a saved one, get the context and column value\n\t\t\t\t$column : map-get($_gridle_columns, $columns);\n\t\t\t\t$context : map-get($column, context);\n\t\t\t\t$columns : map-get($column, columns);\n\t\t\t} @else if type-of($columns) == number {\n\t\t\t\t// check if we have a ratio\n\t\t\t\t@if $columns < 1 {\n\t\t\t\t\t$columns : $context / 100 * ($columns * 100);\n\t\t\t\t}\n\t\t\t\t$columns : $columns / $name-multiplicator;\n\t\t\t} @else {\n\t\t\t\t@error \"the column #{$columns} does not exist...\";\n\t\t\t}\n\n\t\t\t// width :\n\t\t\t$width : 0;\n\t\t\t@if $column-width {\n\t\t\t\t$width : $column-width * $columns;\n\t\t\t} @else {\n\t\t\t\t$width : percentage(1 / $context * $columns);\n\t\t\t}\n\n\t\t\t// if flex\n\t\t\t@include flex-basis($width);\n\t\t\tmax-width : $width;\n\t\t}\n\n\t}\n}\n\n//\n// Grid table\n//\n@mixin _gridle_common_grid_table(\n\t$state : default\n) {\n}\n@mixin gridle_grid_table(\n) {\n\t@include _gridle_call(grid-table) {\n\t}\n}\n\n\n//\n// Row full\n//\n@mixin _gridle_common_row_full(\n\t$state : default\n) {\n}\n@mixin gridle_row_full(\n) {\n\t@include _gridle_call(row-full) {\n\t\t$direction : gridle_get_state_var(direction);\n\t\twidth: 100vw;\n\t\t@if $direction == rtl {\n\t\t\tmargin-right:50% !important;\n\t\t\tmargin-left:0 !important;\n\t\t\t@if $gridle-vendor-prefix {\n\t\t\t\t-webkit-transform: translateX(50%);\n\t\t\t\t-moz-transition: translateX(50%);\n\t\t\t\t-ms-transform: translateX(50%);\n\t\t\t\t-o-transform: translateX(50%);\n\t\t\t}\n\t\t\ttransform: translateX(50%);\n\t\t} @else {\n\t\t\tmargin-left:50% !important;\n\t\t\tmargin-right:0 !important;\n\t\t\t@if $gridle-vendor-prefix {\n\t\t\t\t-webkit-transform: translateX(-50%);\n\t\t\t\t-moz-transition: translateX(-50%);\n\t\t\t\t-ms-transform: translateX(-50%);\n\t\t\t\t-o-transform: translateX(-50%);\n\t\t\t}\n\t\t\ttransform: translateX(-50%);\n\t\t}\n\t\t@if gridle_get_state_var(dir-attribute) {\n\t\t\t[dir=\"ltr\"] & {\n\t\t\t\tmargin-left:50% !important;\n\t\t\t\tmargin-right:0 !important;\n\t\t\t\t@if $gridle-vendor-prefix {\n\t\t\t\t\t-webkit-transform: translateX(-50%);\n\t\t\t\t\t-moz-transition: translateX(-50%);\n\t\t\t\t\t-ms-transform: translateX(-50%);\n\t\t\t\t\t-o-transform: translateX(-50%);\n\t\t\t\t}\n\t\t\t\ttransform: translateX(-50%);\n\t\t\t}\n\t\t\t[dir=\"rtl\"] & {\n\t\t\t\tmargin-right:50% !important;\n\t\t\t\tmargin-left:0 !important;\n\t\t\t\t@if $gridle-vendor-prefix {\n\t\t\t\t\t-webkit-transform: translateX(50%);\n\t\t\t\t\t-moz-transition: translateX(50%);\n\t\t\t\t\t-ms-transform: translateX(50%);\n\t\t\t\t\t-o-transform: translateX(50%);\n\t\t\t\t}\n\t\t\t\ttransform: translateX(50%);\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// grid adapt\n//\n@mixin _gridle_common_grid_adapt(\n\t$state : default\n) {\n\tmax-width: 100%;\n\t@include flexbox;\n\t@include flex(0,1,auto);\n\t@include flex-direction(column); // reset direction for inner elements\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-box-sizing: border-box;\n\t\t-moz-box-sizing: border-box;\n\t}\n\tbox-sizing: border-box;\n\t@include gridle_gutter();\n}\n@mixin gridle_grid_adapt(\n) {\n\t@include _gridle_call(grid-adapt) {\n\t}\n}\n\n\n//\n// Grow\n//\n@mixin _gridle_common_grid_grow(\n\t$state : default\n) {\n\tmax-width: 100%;\n\t@include flexbox;\n\t@include flex(1,1,auto);\n\t@include flex-direction(column); // reset direction for inner elements\t\n\t@if $gridle-vendor-prefix {\n\t\t-webkit-box-sizing: border-box;\n\t\t-moz-box-sizing: border-box;\n\t}\n\tbox-sizing: border-box;\n\t@include gridle_gutter();\n\t\n}\n@mixin gridle_grid_grow(\n) {\n\t@include _gridle_call(grid-grow) {\n\t}\n}\n\n\n//\n// Order\n//\n@mixin _gridle_common_order(\n\t$state : default\n) {\n}\n@mixin gridle_order(\n\t$position\n) {\n\t@include _gridle_call(order) {\n\t\t@include order($position);\n\t}\n}\n\n\n//\n// Clear each\n//\n@mixin _gridle_common_clear_each(\n\t$state : default\n) {\n}\n@mixin gridle_clear_each(\n\t$clearEach,\n\t$clearWhat : both\n) {\n\t@include _gridle_call(clear-each) {\n\t}\n}\n\n\n//\n// Align :\n//\n@mixin _gridle_common_row_align(\n\t$state : default\n) {\n}\n\n@mixin gridle_row_align(\n\t$align : middle\n) {\n\t@include _gridle_call(row-align) {\n\t\t@each $al in $align {\n\t\t\t@if $al == around {\n\t\t\t\t@include justify-content(space-around);\n\t\t\t} @else if $al == between {\n\t\t\t\t@include justify-content(space-between);\n\t\t\t} @else if $al == left {\n\t\t\t\t@include justify-content(flex-start);\n\t\t\t} @else if $al == center {\n\t\t\t\t@include justify-content(center);\n\t\t\t} @else if $al == right {\n\t\t\t\t@include justify-content(flex-end);\n\t\t\t} @else if $al == top {\n\t\t\t\t@include align-items(flex-start);\n\t\t\t} @else if $al == middle {\n\t\t\t\t@include align-items(center);\n\t\t\t} @else if $al == bottom {\n\t\t\t\t@include align-items(flex-end);\n\t\t\t}\n\t\t}\n\t}\n}","//\n// Layout helper\n//\n@mixin gridle_layout(\n\t$layout,\n\t$context : null\n) {\n\t@include gridle_state($context) {\n\t\t@include gridle_row();\n\t\t@each $sel, $l in $layout {\n\t\t\t> #{$sel} {\n\t\t\t\t@include gridle($l);\n\t\t\t}\n\t\t}\n\t}\n}\n\n//\n// Gridle state\n//\n// @param \tstring|map|list \t$states \t\tA list of states, a state name or a state map\n// @param \tboolean \t\t\t$has-parent \tMostly always true, mean that the mixin is used in a css selector and not in css root level\n//\n@mixin gridle_state(\n\t$states,\n\t$has-parent : true\n) {\n\n\t// check if is a min-max witdh query (compatibility layer)\n\t@if type-of($states) == number and type-of($has-parent) == number {\n\n\t\t// get a new state from settings passed\n\t\t$state : gridle_get_state((\n\t\t\tmin-width : $states,\n\t\t\tmax-width : $has-parent\n\t\t));\n\n\t\t// make a query\n\t\t@include gridle_state($state) {\n\t\t\t@content;\n\t\t}\n\n\t} @elseif type-of($states) == list\n\t\tor type-of($states) == string {\n\n\t\t// loop on each states :\n\t\t@each $state in $states\n\t\t{\n\t\t\t// make sure we have the state object\n\t\t\t$state : gridle_get_state($state);\n\n\t\t\t// make gridle state\n\t\t\t@include _gridle_state($state, $has-parent, true) {\n\t\t\t\t@content;\n\t\t\t}\n\t\t}\n\n\t} @elseif type-of($states) == map {\n\n\t\t// get a state from the passed one\n\t\t$state : gridle_get_state($states);\n\n\t\t// make gridle state\n\t\t@include _gridle_state($state, $has-parent, true) {\n\t\t\t@content;\n\t\t}\n\t} @else {\n\t\t@content;\n\t}\n}\n\n\n//\n// Element queries\n//\n@mixin gridle_eq(\n\t$var,\n\t$height : false\n) {\n\t$op : \"min-width\";\n\t@if $height {\n\t\t@if $var < 0 {\n\t\t\t$op : \"max-height\";\n\t\t\t$var : $var * -1;\n\t\t} @else {\n\t\t\t$op : \"min-height\";\n\t\t}\n\t} @else {\n\t\t@if $var < 0 {\n\t\t\t$op : \"max-width\";\n\t\t\t$var : $var * -1;\n\t\t}\n\t}\n\n\t&[#{$op}~=\"#{$var}\"] {\n\t\t@content;\n\t}\n}\n\n\n//\n// Gridle use state\n// This mixin will only set the current state to use and NOT print out any media query\n//\n@mixin gridle_use_state(\n\t$state\n) {\n\t// get the state\n\t$state : gridle_get_state($state);\n\t$stateName : gridle_get_state_var(name, $state);\n\n\t// save the current state\n\t$savedState : $_gridle_current_state;\n\t$savedStateName : $_gridle_current_stateName;\n\n\t// set the current state\n\t$_gridle_current_state : $state !global;\n\t$_gridle_current_stateName : $stateName !global;\n\n\t// generate content\n\t@content;\n\n\t// reset the state\n\t$_gridle_current_state : $savedState !global;\n\t$_gridle_current_stateName : $savedStateName !global;\n}\n\n\n//\n// Gridle selector\n//\n@mixin gridle_selector(\n\t$for,\n\t$states : null\n) {\n\t#{gridle_selector($for, $states)} {\n\t\t@content;\n\t}\n}\n\n\n//\n// Gridle helper\n//\n@mixin gridle($layout) {\n\t$map : _gridle_get_gridle_set_map_from_list($layout);\n\t@if type-of($map) == map {\n\t\t@include gridle_set($map);\n\t}\n}\n\n\n//\n// Helper to apply multiple config for a certain state with one mixin\n//\n@mixin gridle_set(\n\t$settings,\n\t$state : current\n) {\n\t// init if needed\n\t@include gridle_init();\n\t// wrap in media query\n\t@include gridle_state($state) {\n\t\t@include _gridle_set($settings);\n\t}\n}\n@mixin _gridle_set(\n\t$settings\n) {\n\t// loop on each settings\n\t@each $settingName, $settingValue in $settings\n\t{\n\t\t$sn : unquote(\"#{$settingName}\");\n\t\t$sv : $settingValue;\n\n\t\t// check if setting name is a state :\n\t\t@if gridle_has_state($sn) {\n\t\t\t// process the state\n\t\t\t@include gridle_set($sv, $sn);\n\t\t} @else {\n\t\t\t@if $sn == container {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_container();\n\t\t\t\t}\n\t\t\t} @else if $sn == grid {\n\t\t\t\t@if length($sv) == 2 {\n\t\t\t\t\t@include gridle_grid(nth($sv,1), nth($sv,2));\n\t\t\t\t} @else if type-of($sv) == list and length($sv) == 1 {\n\t\t\t\t\t@include gridle_grid(nth($sv,1));\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_grid($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == grid-grow {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_grid_grow();\n\t\t\t\t}\n\t\t\t} @else if $sn == grid-adapt {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_grid_adapt();\n\t\t\t\t}\n\t\t\t} @else if $sn == grid-table {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_grid_table();\n\t\t\t\t}\n\t\t\t} @else if $sn == push {\n\t\t\t\t@if length($sv) == 2 {\n\t\t\t\t\t@include gridle_push(nth($sv,1), nth($sv,2));\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_push($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == pull {\n\t\t\t\t@if length($sv) == 2 {\n\t\t\t\t\t@include gridle_pull(nth($sv,1), nth($sv,2));\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_pull($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == prefix {\n\t\t\t\t@if length($sv) == 2 {\n\t\t\t\t\t@include gridle_prefix(nth($sv,1), nth($sv,2));\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_prefix($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == suffix {\n\t\t\t\t@if length($sv) == 2 {\n\t\t\t\t\t@include gridle_suffix(nth($sv,1), nth($sv,2));\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_suffix($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == clear-each {\n\t\t\t\t@if length($sv) == 2 {\n\t\t\t\t\t@include gridle_clear_each(nth($sv,1), nth($sv,2));\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_clear_each($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == grid-centered and $sv == true {\n\t\t\t\t@include gridle_grid_centered();\n\t\t\t} @else if $sn == row and $sv == true {\n\t\t\t\t@include gridle_row();\n\t\t\t} @else if $sn == row-full and $sv == true {\n\t\t\t\t@include gridle_row_full();\n\t\t\t} @else if $sn == col and $sv == true {\n\t\t\t\t@include gridle_col();\n\t\t\t} @else if $sn == row-align {\n\t\t\t\t@include gridle_row_align($sv);\n\t\t\t} @else if $sn == nowrap {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_nowrap();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_wrap();\n\t\t\t\t}\n\t\t\t} @else if $sn == wrap {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_wrap();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_nowrap();\n\t\t\t\t}\n\t\t\t} @else if $sn == order {\n\t\t\t\t@include gridle_order($sv);\n\t\t\t} @else if $sn == hide {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_hide();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_show();\n\t\t\t\t}\n\t\t\t} @else if $sn == show {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_show();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_hide();\n\t\t\t\t}\n\t\t\t} @else if $sn == visible {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_visible();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_not_visible();\n\t\t\t\t}\n\t\t\t} @else if $sn == not-visible {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_not_visible();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_visible();\n\t\t\t\t}\n\t\t\t} @else if $sn == show-inline {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_show_inline();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_hide();\n\t\t\t\t}\n\t\t\t} @else if $sn == float {\n\t\t\t\t@include gridle_float($sv);\n\t\t\t} @else if $sn == clear {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_clear();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_clear($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == no-gutter {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_no_gutter();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_no_gutter($sv);\n\t\t\t\t}\n\t\t\t} @else if $sn == gutter or $sn == gutter-width {\n\t\t\t\t@if $sv == true {\n\t\t\t\t\t@include gridle_gutter();\n\t\t\t\t} @else {\n\t\t\t\t\t@include gridle_gutter($sv);\n\t\t\t\t}\n\t\t\t} @else {\n\t\t\t\t// we do nothing\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Push\n//\n@mixin _gridle_common_push(\n\t$state : default\n) {\n\t// css\n\tposition:relative;\n\t// handle direction attribute\n\t@if gridle_get_state_var(dir-attribute, $state) {\n\t\t[dir=\"rtl\"] & {\n\t\t\tleft: auto;\n\t\t}\n\t\t[dir=\"ltr\"] & {\n\t\t\tright: auto;\n\t\t}\n\t}\n}\n@mixin gridle_push(\n\t$columns,\n\t$context : null\n) {\n\t@include _gridle_call(push) {\n\t\t// variables :\n\t\t@if type-of($context) != number {\n\t\t\t$context : gridle_get_state_var(context);\n\t\t}\n\t\t$direction : gridle_get_state_var(direction);\n\t\t$name-multiplicator : gridle_get_state_var(name-multiplicator);\n\t\t$column-width : gridle_get_state_var(column-width);\n\n\t\t// vars :\n\t\t$width : 0;\n\t\t@if $column-width {\n\t\t\t$width : $column-width * ($columns / $name-multiplicator);\n\t\t} @else {\n\t\t\t$width : percentage(1 / $context) * ($columns / $name-multiplicator);\n\t\t}\n\t\t// @if $direction == rtl { $width : $width*-1; }\n\t\t// left:$width;\n\t\t// @if $direction == ltr {\n\t\t// \t[dir=\"rtl\"] & {\n\t\t// \t\tleft: $width * -1;\n\t\t// \t}\n\t\t// }\n\n\t\t// set value :\n\t\t@if $direction == rtl { right:$width;left:auto; }\n\t\t@else { left:$width;right:auto; }\n\t\t// handle direction attribute\n\t\t@if gridle_get_state_var(dir-attribute) {\n\t\t\t[dir=\"rtl\"] & {\n\t\t\t\tright: $width;\n\t\t\t}\n\t\t\t[dir=\"ltr\"] & {\n\t\t\t\tleft: $width;\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Pull\n//\n@mixin _gridle_common_pull(\n\t$state : default\n) {\n\t// css\n\tposition:relative;\n\t// handle direction attribute\n\t@if gridle_get_state_var(dir-attribute, $state) {\n\t\t[dir=\"rtl\"] & {\n\t\t\tright: auto;\n\t\t}\n\t\t[dir=\"ltr\"] & {\n\t\t\tleft: auto;\n\t\t}\n\t}\n}\n@mixin gridle_pull(\n\t$columns,\n\t$context : null\n) {\n\t@include _gridle_call(pull) {\n\t\t// variables :\n\t\t@if type-of($context) != number {\n\t\t\t$context : gridle_get_state_var(context);\n\t\t}\n\t\t$direction : gridle_get_state_var(direction);\n\t\t$name-multiplicator : gridle_get_state_var(name-multiplicator);\n\t\t$column-width : gridle_get_state_var(column-width);\n\n\t\t// vars :\n\t\t$width : 0;\n\t\t@if $column-width {\n\t\t\t$width : $column-width * ($columns / $name-multiplicator);\n\t\t} @else {\n\t\t\t$width : percentage(1 / $context) * ($columns / $name-multiplicator);\n\t\t}\n\t\t// @if $direction == rtl { $width : $width*-1; }\n\t\t// right:$width;\n\t\t// @if $direction == ltr {\n\t\t// \t[dir=\"rtl\"] & {\n\t\t// \t\tleft: $width * -1;\n\t\t// \t}\n\t\t// }\n\n\t\t// set value :\n\t\t@if $direction == rtl { left:$width;right:auto; }\n\t\t@else { right:$width;left:auto; }\n\t\t// handle direction attribute\n\t\t@if gridle_get_state_var(dir-attribute) {\n\t\t\t[dir=\"rtl\"] & {\n\t\t\t\tleft: $width;\n\t\t\t}\n\t\t\t[dir=\"ltr\"] & {\n\t\t\t\tright: $width;\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Prefix\n//\n@mixin _gridle_common_prefix(\n\t$state : default\n) {\n\t// handle direction attribute\n\t@if gridle_get_state_var(dir-attribute, $state) {\n\t\t[dir=\"rtl\"] & {\n\t\t\tmargin-left: auto;\n\t\t}\n\t\t[dir=\"ltr\"] & {\n\t\t\tmargin-right: auto;\n\t\t}\n\t}\n}\n@mixin gridle_prefix(\n\t$columns,\n\t$context : null\n) {\n\t@include _gridle_call(prefix) {\n\t\t// vars :\n\t\t@if type-of($context) != number {\n\t\t\t$context : gridle_get_state_var(context);\n\t\t}\n\t\t$direction : gridle_get_state_var(direction);\n\t\t$name-multiplicator : gridle_get_state_var(name-multiplicator);\n\t\t$column-width : gridle_get_state_var(column-width);\n\n\t\t// vars :\n\t\t$width : 0;\n\t\t@if $column-width {\n\t\t\t$width : $column-width * ($columns / $name-multiplicator);\n\t\t} @else {\n\t\t\t$width : percentage(1 / $context) * ($columns / $name-multiplicator);\n\t\t}\n\n\t\t// set value :\n\t\t@if $direction == rtl { margin-right:$width; }\n\t\t@else { margin-left:$width; }\n\t\t// handle direction attribute\n\t\t@if gridle_get_state_var(dir-attribute) {\n\t\t\t[dir=\"rtl\"] & {\n\t\t\t\tmargin-right: $width;\n\t\t\t}\n\t\t\t[dir=\"ltr\"] & {\n\t\t\t\tmargin-left: $width;\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Suffix\n//\n@mixin _gridle_common_suffix(\n\t$state : default\n) {\n\t@if gridle_get_state_var(dir-attribute, $state) {\n\t\t[dir=\"rtl\"] & {\n\t\t\tmargin-right: auto;\n\t\t}\n\t\t[dir=\"ltr\"] & {\n\t\t\tmargin-left: auto;\n\t\t}\n\t}\n}\n@mixin gridle_suffix(\n\t$columns,\n\t$context : null\n) {\n\t@include _gridle_call(suffix) {\n\t\t// vars :\n\t\t@if type-of($context) != number {\n\t\t\t$context : gridle_get_state_var(context);\n\t\t}\n\t\t$direction : gridle_get_state_var(direction);\n\t\t$name-multiplicator : gridle_get_state_var(name-multiplicator);\n\t\t$column-width : gridle_get_state_var(column-width);\n\n\t\t// vars :\n\t\t$width : 0;\n\t\t@if $column-width {\n\t\t\t$width : $column-width * ($columns / $name-multiplicator);\n\t\t} @else {\n\t\t\t$width : percentage(1 / $context) * ($columns / $name-multiplicator);\n\t\t}\n\n\t\t// set value :\n\t\t@if $direction == rtl { margin-left:$width; }\n\t\t@else { margin-right:$width; }\n\n\t\t// handle direction attribute\n\t\t@if gridle_get_state_var(dir-attribute) {\n\t\t\t[dir=\"rtl\"] & {\n\t\t\t\tmargin-left: $width;\n\t\t\t}\n\t\t\t[dir=\"ltr\"] & {\n\t\t\t\tmargin-right: $width;\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Row debug\n//\n@mixin _gridle_common_row_debug(\n\t$state : default\n) {\n}\n@mixin gridle_row_debug(\n\t$states : null\n) {\n\t@include _gridle_call(row-debug) {\n\t\t// variables :\n\t\t$context : gridle_get_state_var(context);\n\n\t\tposition:relative;\n\t\tz-index:99999;\n\t\toverflow:hidden;\n\n\t\t&:before {\n\t\t\tpointer-events: none;\n\t\t\tcontent:'';\n\t\t\tposition:absolute;\n\t\t\ttop:0; left:0;\n\t\t\twidth:100%; height:99999px;\n\t\t\t// vars :\n\t\t\t$width : percentage(1 / $context);\n\t\t\tbackground: linear-gradient(to right, rgba(0,0,0,0) 50% , rgba(0,0,0,.02) 50%); // Standard syntax\n\t\t\tbackground-size:($width*2) 100%;\n\t\t\tz-index:99999;\n\t\t}\n\t}\n}\n\n\n//\n// Debug\n//\n@mixin gridle_debug(\n\t$what,\n\t$output : shell\n) {\n\t@if $gridle-debug {\n\t\t@if $output == shell {\n\t\t\t@debug(inspect($what));\n\t\t} @else {\n\t\t\t@if type-of($what) == map {\n\t\t\t\t@include gridle_debug_map($what);\n\t\t\t} @else {\n\t\t\t\t@_ {\n\t\t\t\t\t#{type-of($what)} : inspect($what);\n\t\t\t\t}\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Debug a map\n//\n@mixin gridle_debug_map($map, $name : '') {\n\t@at-root {\n\t\t@_ #{$name} {\n\t\t\t@each $key, $value in $map {\n\t\t\t\t@if type-of($value) == map {\n\t\t\t\t\t@include gridle_debug_map($value, $key);\n\t\t\t\t} @else {\n\t\t\t\t\t#{$key}: inspect($value);\n\t\t\t\t}\n\t\t\t}\n\t\t}\n\t}\n}\n\n\n//\n// Container\n//\n@mixin _gridle_common_container(\n\t$state : default\n) {\n\t&:after {\n\t\tcontent: \"\";\n\t\tdisplay: table;\n\t\tclear: both;\n\t}\n\n}\n@mixin gridle_container(\n) {\n\t@include _gridle_call(container) {\n\t}\n}\n\n\n//\n// Centered\n//\n@mixin _gridle_common_grid_centered(\n\t$state : default\n) {\n\tdisplay:block !important;\n\tfloat:none !important;\n\tmargin-left:auto !important;\n\tmargin-right:auto !important;\n\tclear:both !important;\n}\n@mixin gridle_grid_centered(\n) {\n\t@include _gridle_call(grid-centered) {\n\t}\n}\n\n\n//\n// Hide\n//\n@mixin _gridle_common_hide(\n\t$state : default\n) {\n}\n@mixin gridle_hide(\n) {\n\t@include _gridle_call(hide) {\n\t\tdisplay:none;\n\t}\n}\n\n\n//\n// Not visible on\n//\n@mixin _gridle_common_not_visible(\n\t$state : default\n) {\n}\n@mixin gridle_not_visible(\n) {\n\t@include _gridle_call(not-visible) {\n\t\tvisibility:hidden;\n\t}\n}\n\n\n//\n// Show on\n//\n// @param \tString \t$media \tWhat to hide (one of the 3 state classes name)\n//\n@mixin _gridle_common_show(\n\t$state : default\n) {\n}\n@mixin gridle_show(\n) {\n\t@include _gridle_call(show) {\n\t\tdisplay:block;\n\t}\n}\n\n\n//\n// Show inline\n//\n// @param \tString \t\t$state \t\tThe state name\n//\n@mixin _gridle_common_show_inline(\n\t$state : default\n) {\n}\n@mixin gridle_show_inline(\n) {\n\t@include _gridle_call(show-inline) {\n\t\tdisplay:inline-block;\n\t}\n}\n\n\n//\n// Visible on :\n// @param \tString \t$media \tOn what state\n//\n@mixin _gridle_common_visible(\n\t$state : default\n) {\n}\n@mixin gridle_visible(\n) {\n\t@include _gridle_call(visible) {\n\t\tvisibility:visible;\n\t}\n}\n\n\n//\n// Float :\n//\n@mixin _gridle_common_float(\n\t$state : default\n) {\n}\n@mixin gridle_float(\n\t$float-direction : left\n) {\n\t@include _gridle_call(float) {\n\t\tfloat:#{$float-direction};\n\t}\n}\n\n\n//\n// Gridle clear :\n//\n@mixin _gridle_common_clear(\n\t$state : default\n) {\n}\n@mixin gridle_clear(\n\t$clear-direction : both\n) {\n\t@include _gridle_call(clear) {\n\t\tclear:#{$clear-direction};\n\t}\n}\n\n\n//\n// Gridle no gutter :\n//\n@mixin _gridle_common_no_gutter(\n\t$state : default\n) {\n}\n@mixin gridle_no_gutter(\n\t$side : top right bottom left\n) {\n\t@include _gridle_call(no-gutter) {\n\t\t@each $s in $side {\n\t\t\tpadding-#{$s} : 0;\n\t\t}\n\t}\n}\n\n\n//\n// Gridle gutter :\n//\n@mixin _gridle_common_gutter(\n\t$state : default\n) {\n}\n@mixin gridle_gutter(\n\t$side-or-size : top right bottom left\n) {\n\t@include _gridle_call(gutter) {\n\n\t\t// get a gutter map\n\t\t$gutters : _gridle_forge_gutters_map($side-or-size);\n\n\t\t// check that we have a gutter map\n\t\t@each $side, $value in $gutters {\n\t\t\t@if $value and $value > 0 {\n\t\t\t\tpadding-#{$side} : $value;\n\t\t\t}\n\t\t}\n\n\t}\n}\n","// |------------------------------------------------------\n// |------------------------------------------------------\n// | Settings mixins\n// |------------------------------------------------------\n// |------------------------------------------------------\n\n//\n// Setup\n//\n@mixin gridle_setup(\n\t$settings : ()\n) {\n\t// prepare state\n\t$settings : _gridle_prepare_state_settings($settings);\n\n\t// extend default settings\n\t$_gridle-settings : map-merge((\n\t\tname : default,\n\t\tmin-width : null,\n\t\tmax-width : null,\n\t\tquery : null,\n\t\tclasses : true,\n\t\tcontext : 12,\n\t\tcolumn-width : null,\n\t\tgutter-width : 20px,\n\t\tgutter-height : 0,\n\t\tgutter-top : 0,\n\t\tgutter-right : 10px,\n\t\tgutter-bottom : 0,\n\t\tgutter-left : 10px,\n\t\tdirection : ltr,\n\t\tdir-attribute : false,\n\t\tname-multiplicator : 1,\n\t\tstates-classes : false,\n\t\tclasses-prefix : null\n\t), $settings) !global;\n\n\t// register default state :\n\t@include gridle_register_state(default, $_gridle-settings);\n\n\t// set current state to default\n\t$_gridle_current_state : $_gridle-settings !global;\n\t$_gridle_current_stateName : default !global;\n\n}\n\n//\n// Register a state\n//\n@mixin gridle_register_state(\n\t$name,\n\t$settings : ()\n) {\n\t// prepare state\n\t$settings : _gridle_prepare_state_settings($settings);\n\n\t// settings :\n\t$settings : map-merge($_gridle-settings, $settings);\n\n\t// set name :\n\t$settings : map-set($settings, name, $name);\n\n\t// gutter sizes :\n\t$gutter-top : map-get($settings, gutter-top);\n\t$gutter-right : map-get($settings, gutter-right);\n\t$gutter-bottom : map-get($settings, gutter-bottom);\n\t$gutter-left : map-get($settings, gutter-left);\n\n\t// add state in maps :\n\t$_gridle_states : map-set($_gridle_states, $name, $settings) !global;\n\n\t// add rules if gutter is different\n\t@if $gutter-top != map-get($_gridle-settings, gutter-top)\n\t\tor $gutter-right != map-get($_gridle-settings, gutter-right)\n\t\tor $gutter-bottom != map-get($_gridle-settings, gutter-bottom)\n\t\tor $gutter-left != map-get($_gridle-settings, gutter-left) {\n\t\t$gutters : _gridle_forge_gutters_map(top right bottom left, $name);\n\t\t@include gridle_apply_css_for(grid grid-grow grid-adapt, (\n\t\t\tgridle_gutter : top right bottom left\n\t\t), $name);\n\t}\n\n\t// add rules if direction is different\n\t$direction : map-get($settings, direction);\n\t@if $direction != map-get($_gridle-settings, direction) {\n\t\t$float : left;\n\t\t@if $direction == rtl {\n\t\t\t$float : right;\n\t\t}\n\t\t@include gridle_apply_css_for(grid, (\n\t\t\tdirection : $direction,\n\t\t\tfloat : $float\n\t\t), $name);\n\t}\n}\n\n\n//\n//  Register a clear each class\n//\n@mixin gridle_register_clear_each(\n\t$count,\n\t$clearWhat\n) {\n\t// create the clear map :\n\t$classMap : (\n\t\tclearEach : $count,\n\t\tclearWhat : $clearWhat\n\t);\n\n\t// append to map :\n\t$_gridle_clear_classes : map-set($_gridle_clear_classes, $count, $classMap) !global;\n}\n\n\n//\n// Register a special class\n//\n@mixin gridle_register_column(\n\t$name,\n\t$columns,\n\t$context\n) {\n\t// create a column :\n\t$col : _gridle_create_column($name, $columns, $context);\n\n\t// add column in maps :\n\t$_gridle_columns : map-set($_gridle_columns, $name, $col) !global;\n}\n\n\n//\n// Set a classmap\n//\n@mixin gridle_set_classname_map(\n\t$for,\n\t$pattern\n) {\n\t// get the map\n\t$map : map-get($_gridle-packages, $for);\n\t// set the new classname\n\t$map : map-set($map, classname, $pattern);\n\t// set the classmap\n\t$_gridle-packages : map-set($_gridle-packages, $for, $map) !global;\n}\n\n\n//\n// Set a generic selector\n//\n@mixin gridle_set_generic_selector(\n\t$for,\n\t$selector\n) {\n\t// get the map\n\t$map : map-get($_gridle-packages, $for);\n\t// set the new classname\n\t$map : map